INSERT INTO semantic_types(id,name,pos,definition) VALUES ("1","Animate","NOUN","Determina classes de seres nomeados por substantivos e que têm existência independente, cujas instâncias são animais não humanos. Instâncias desta classe são indivíduos contáveis.");
INSERT INTO semantic_types(id,name,pos,definition) VALUES ("2","Human","NOUN","Metaclasse que determina classes de seres nomeados por substantivos e que tém existéncia independente, cujas instâncias são animais humanos; tais instâncias são indivíduos contáveis.");
INSERT INTO semantic_types(id,name,pos,definition) VALUES ("3","Kin","NOUN","Metaclasse relacionada à metaclasse Human, cujas metapropridades determinam a distinção do papel desempenhado por um Human em relação aos outros membros de um grupo familiar a que ele pertence.");
INSERT INTO semantic_types(id,name,pos,definition) VALUES ("4","Rank","NOUN","Expressa a função ou papel desempenhado por um Human em relação a outros membros de um grupo a que ele pertença, só que, neste caso, esse grupo não é familiar.");
INSERT INTO semantic_types(id,name,pos,definition) VALUES ("5","Social Group","NOUN","Substantivos cujos referentes são grupos de indivíduos vistos como uma unidade funcional unicamente identificada.");
INSERT INTO semantic_types(id,name,pos,definition) VALUES ("6","Parts","NOUN","Metaclasse que determina classes de coisas que podem ser vistas como partes de outras coisas ou seres, incluindo as partes corpóreas.");
INSERT INTO semantic_types(id,name,pos,definition) VALUES ("7","Inanimate","NOUN","Metaclasse cujas meta propriedades determinam classes de coisas ou seres inanimados (literalmente, os 'sem alma').");
INSERT INTO semantic_types(id,name,pos,definition) VALUES ("8","Artefact","NOUN","Metaclasse cujas metapropriedades determinam classes de coisas fabricadas pelo homem, ou seja, que não existem na natureza.");
INSERT INTO semantic_types(id,name,pos,definition) VALUES ("9","Celestial & Weather","NOUN","Metaclasse cujas metapropriedades determinam classes de referentes que são corpos celestes ou ligados ao tempo, como chuva e vento.");
INSERT INTO semantic_types(id,name,pos,definition) VALUES ("10","Environment","NOUN","Metaclasse cujas metapropriedades determinam classes de elementos naturais em sua maioria pertencente ao reino mineral e vistos como uma massa contínua e indiferenciável, como por exemplo ouro, ferro, água, etc.");
INSERT INTO semantic_types(id,name,pos,definition) VALUES ("11","Flora","NOUN","Metaclasse cujas metapropriedades determinam seres do mundo natural cujos referentes pertencem ao reino vegetal.");
INSERT INTO semantic_types(id,name,pos,definition) VALUES ("12","Time","NOUN","Metaclasse cujas metapropriedades determinam classes que referem-se a unidades do tempo.");
INSERT INTO semantic_types(id,name,pos,definition) VALUES ("13","Place","NOUN","Metaclasse cujas metapropriedades determinam classes que referem- se a localizações, como os nomes dos pontos cardeais, por exemplo.");
INSERT INTO semantic_types(id,name,pos,definition) VALUES ("14","Quantity","NOUN","Metaclasse cujas metapropriedades determinam classes de conceitos que referenciam quantidades, incluindo unidades de medida.");
INSERT INTO semantic_types(id,name,pos,definition) VALUES ("15","Variety","NOUN","Metaclasse cujas metapropriedades determinam classes de conceitos que referenciam variedades de tipos e formas, incluindo formas geométricas.");
INSERT INTO semantic_types(id,name,pos,definition) VALUES ("16","Language","NOUN","Metaclasse cujas metapropriedades determinam classes que referenciam elementos lingüísticos, como palavra, sentença, oração, substantivo, etc.");
INSERT INTO semantic_types(id,name,pos,definition) VALUES ("17","Other","NOUN","Metaclasse cujas metapropriedades determinam classes de substantivos que referenciam conceitos abstratos como idéia, unidade, método, resultado, verdade, etc.");
INSERT INTO semantic_types(id,name,pos,definition) VALUES ("18","Activity","NOUN","Metaclasse que determina atividades; são referéncias abstratas a ações, cujos signos derivam-se de verbos.");
INSERT INTO semantic_types(id,name,pos,definition) VALUES ("19","States","NOUN","Metaclasse cujas metapropriedades determinam estados físicos ou mentais de indivíduos, ou ainda propriedades individuais, como, por exemplo, prazer, força, dor, saudade, etc.");
INSERT INTO semantic_types(id,name,pos,definition) VALUES ("20","Speech Acts","NOUN","Metaclasse cujas metapropriedades determinam atos de fala, tais como, ordem, pergunta, conversa, promessa, etc.");
